@import '/src/styles.scss';

.host {
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 5px;
  background-color: #fff;
  border: 1px solid $clr_neutral_300;

  &.selected_indicator {
    border-color: #5044b8;
    // transition: .2s ease-in;

    &::before {
      content: '';
      z-index: 1;
      background-color: #5044b8;
      width: 16px;
      height: 16px;
      display: flex;
      justify-content: center;
      font-size: 12px;
      line-height: 20px;
      color: #fff;
      font-weight: bold;
      position: absolute;
      inset-inline-start: 0;
      top: 0;
      border-radius: 0;
      border-end-end-radius: 4px;
      border-start-start-radius: 4px;
    }

    &.multi {
      &::before {
        content: attr(data-count);
      }
    }

    .toggler {
      padding-inline-start: 26px;
    }
  }

  .toggler {
    display: flex;
    justify-content: space-between;
    align-items: center;
    column-gap: 10px;
    width: 100%;
    background-color: #fff;
    border: none;
    outline: none;
    border-radius: 4px;
    position: relative;
    height: 40px;
    padding: 0 12px;
    cursor: pointer;
    font-size: 16px;
    line-height: 20px;

    &.has-badges {
      padding-inline-start: 4px;
    }

    .placeholder {
      display: flex;
      column-gap: 5px;
    }

    .badges {
      display: flex;
      justify-content: flex-start;
      align-items: center;
      column-gap: 4px;

      .badge {
        background: $clr_primary_50;
        padding: 5px 12px;
        border-radius: $radius_sm;
        border: 1px solid $clr_primary_200;
        color: $clr_primary_700;
        display: flex;
        justify-content: space-between;
        height: 32px;
        cursor: default;
        font-size: 16px;
        line-height: 16px;
        column-gap: 4px;

        &_text {
          white-space: nowrap;
          text-overflow: ellipsis;
          overflow: hidden;
          max-width: 90px;
          color: $clr_primary_700;
          line-height: 20px;
        }

        &_close {
          cursor: pointer;
          line-height: unset;
          font-size: 16px;
          font-weight: bold;
          color: $clr_neutral_900;
          line-height: 20px;
        }
      }
    }

    .arrow_down_icon {
      position: relative;
      width: 24px;
      height: 24px;

      &::before {
        content: '';
        position: absolute;
        left: 4px;
        width: 0;
        height: 0;
        border: 8px solid transparent;
        border-top: 8px solid #231F20;
        border-bottom: 0;
        top: 8px;
      }

      &::after {
        content: '';
        position: absolute;
        left: 4px;
        width: 0;
        height: 0;
        border: 8px solid transparent;
        border-top: 8px solid #fff;
        border-bottom: 0;
        top: 5px;
      }
    }
  }

  .overflow {
    cursor: default !important;
    display: none;

    @media screen and (max-width: 767px) {
      display: unset;
      position: fixed;
      top: 0;
      bottom: 0;
      right: 0;
      left: 0;
      background: rgb(0 0 0 / 75%);
      z-index: 998;
      backdrop-filter: blur(3px);
    }
  }

  .list {
    position: absolute;
    background: #fff;
    top: calc(100% + 8px);
    inset-inline-start: 0;
    z-index: 1;
    list-style: none;
    margin: 0;
    padding: 0;
    border-radius: 5px;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.2), 0px 2px 10px rgba(0, 0, 0, 0.1);
    cursor: default;
    border: 1px solid #E4E9F2;
    box-sizing: border-box;
    text-align: start;
    width: max-content;
    min-width: 100%;
    overflow: hidden;

    @media screen and (max-width: 768px) {
      min-width: 90%;
      width: 90%;
      position: fixed;
      top: 50%;
      right: 5%;
      transform: translateY(-50%);
      font-size: 1rem;
      box-shadow: 0 0 10px 3px rgba(0, 0, 0, 0.1);
      z-index: 999;
    }

    &_content {
      overflow-y: auto;
      max-height: 300px;
      width: 100%;
      border-radius: 5px;
      overscroll-behavior: contain;
      gap: 4px;
      display: flex;
      flex-direction: column;
      padding: 4px;
    }

    hr {
      margin: 0;
      border: none;
      height: 1px;
      background: $clr_neutral_300;
    }

    .search_section {
      text-align: center;
      padding: 0 10px;
      text-align: center;
      padding: 10px 10px 0;
      position: sticky;
      top: 0;
      width: 100%;
      background: white;

      input {
        border-radius: 5px;
        padding: 6px 8px;
        border: 1px solid #e4e9fd;
        box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, 0.05);
        width: 100%;
        margin: 0;
      }

      hr {
        margin: 10px 0 5px;
      }
    }

    .lite {
      color: #9f9f9f;
      font-weight: normal;
    }

    .item_selected {
      color: #5044b8;
    }

    &_item {
      // transition: 0.2s ease-in;
      padding: 0 12px;
      display: flex;
      display: -webkit-flex;
      justify-content: flex-start;
      align-items: center;
      column-gap: 8px;
      cursor: pointer;
      height: 40px;
      border-radius: $radius_sm;

      @media screen and (max-width: 768px) {
        padding: 8px 20px;
      }

      &.select_all {
        border-bottom: 1px solid #E4E9F2;
      }

      .radio-btn,
      .check-box {
        min-width: 14px;
        min-height: 14px;
        position: relative;
        border-radius: 10px;
        outline: 2px solid #e4e9f2;
        // transition: .2s ease-in;
        box-shadow: inset 0px 0px 1px 1px rgb(0 0 0 / 5%);

        &::before {
          content: "";
          width: 100%;
          height: 100%;
          position: absolute;
          top: 0;
          left: 0;
          border: 2px solid #fff;
          border-radius: 50%;
          // transition: .2s ease-in;
        }
      }

      .check-box {
        min-width: 18px;
        min-height: 18px;
        border-radius: 3px;

        &::before {
          border-radius: 4px;
        }
      }

      .selected {
        &::before {
          background-color: #5044b8;
        }
      }

      &:hover {
        background-color: $clr_primary_50;

        .check-box,
        .radio-btn {
          outline-color: #5044b8;
        }
      }

      .default {
        color: #9f9f9f;
        font-weight: normal;
      }

      i {
        margin-inline-start: auto;
      }
    }
  }

